// BoxJs辅助脚本，用于查看和管理Quantumult X请求捕获的日志

const $ = new Env('Quantumult X请求捕获调试');
const logKey = 'qx_debug_log';
const configKey = 'qx_debug_config';
const serverKey = 'qx_debug_server';
const enabledKey = 'qx_debug_enabled';

// 主函数
!(async () => {
  // 检查环境
  if (typeof $request !== 'undefined') {
    await handleRequest();
  } else {
    await handleBoxJsCommand();
  }
})()
.catch((e) => {
  $.log('', `❌ ${$.name}, 错误!`, e);
})
.finally(() => {
  $.done();
});

// 处理BoxJs的HTTP请求
async function handleRequest() {
  const method = $request.method;
  const url = $request.url;
  
  if (url.includes('/debug/clear')) {
    // 清除日志
    $.setdata('', logKey);
    $.msg($.name, '日志已清除', '所有调试日志记录已清除');
  } else if (url.includes('/debug/test')) {
    // 测试连接
    const server = $.getdata(serverKey) || 'http://your-server:3000';
    try {
      const result = await testConnection(server);
      $.msg($.name, '测试连接', `服务器: ${server}\n状态: ${result}`);
    } catch (e) {
      $.msg($.name, '测试连接失败', `服务器: ${server}\n错误: ${e.message}`);
    }
  } else {
    // 显示日志
    const logs = $.getdata(logKey) || '暂无日志记录';
    const response = {
      body: logs,
      headers: {
        'Content-Type': 'text/plain;charset=utf-8',
      },
    };
    $.done(response);
  }
}

// 处理BoxJs UI命令
async function handleBoxJsCommand() {
  // 获取参数
  const action = $.getdata('action') || '';
  
  switch (action) {
    case 'clear_log':
      $.setdata('', logKey);
      $.msg($.name, '已清除日志', '所有调试日志记录已清除');
      break;
    case 'test_server':
      const server = $.getdata(serverKey) || 'http://your-server:3000';
      try {
        const result = await testConnection(server);
        $.msg($.name, '测试服务器连接', `服务器: ${server}\n状态: ${result}`);
      } catch (e) {
        $.msg($.name, '连接服务器失败', `服务器: ${server}\n错误: ${e.message}`);
      }
      break;
    case 'save_config':
      const server = $.getdata('qx_debug_server');
      const enabled = $.getdata('qx_debug_enabled');
      const config = {
        server,
        enabled: enabled === 'true',
        updated: new Date().toISOString()
      };
      $.setdata(JSON.stringify(config), configKey);
      $.msg($.name, '配置已保存', `服务器: ${server}\n调试: ${enabled === 'true' ? '开启' : '关闭'}`);
      break;
    default:
      // 显示当前配置
      const currentConfig = $.getdata(configKey) || '{}';
      let config;
      try {
        config = JSON.parse(currentConfig);
      } catch (e) {
        config = {};
      }
      $.msg($.name, '当前配置', `服务器: ${config.server || '未设置'}\n调试: ${config.enabled ? '开启' : '关闭'}\n更新时间: ${config.updated || '未知'}`);
  }
}

// 测试与服务器的连接
function testConnection(server) {
  return new Promise((resolve, reject) => {
    const url = `${server}/health`;
    const timeout = setTimeout(() => {
      reject(new Error('请求超时'));
    }, 5000);
    
    $.get({url}, (err, resp, data) => {
      clearTimeout(timeout);
      if (err) {
        reject(err);
        return;
      }
      
      try {
        const result = JSON.parse(data);
        if (result.status === 'ok') {
          resolve('连接成功');
        } else {
          resolve(`连接成功但状态异常: ${data}`);
        }
      } catch (e) {
        reject(new Error(`响应解析失败: ${e.message}`));
      }
    });
  });
}

// 辅助函数，写入日志
function appendLog(message) {
  const timestamp = new Date().toISOString();
  const logMessage = `${timestamp} - ${message}\n`;
  
  // 获取现有日志
  let logs = $.getdata(logKey) || '';
  // 限制日志大小，防止过大
  if (logs.length > 10000) {
    logs = logs.substring(logs.length - 8000);
    logs = `...(已截断前面内容)\n${logs}`;
  }
  
  // 添加新日志
  logs += logMessage;
  $.setdata(logs, logKey);
}

